import React, { useState } from "react";
import {
  Box,
  Typography,
  TextField,
  Button,
  CircularProgress,
} from "@mui/material";
import colors from "../assets/styles/colors";
import auth from "../models/auth";
import { signIn } from "../services/auth.service";
import { popAlert } from "../utils/alerts";
import { useDispatch } from "react-redux";
import { authActions } from "../store/authSlice";

const SignIn = () => {
  const dispatch = useDispatch();

  const [inputs, setInputs] = useState(auth);
  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);

  //login sign in submit function
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    const response = await signIn(inputs);

    if (response.success) {
      setLoading(false);
      dispatch(authActions.login(response.data));
      response?.data?.message &&
        popAlert("Success!", response?.data?.message, "success").then((res) => {
          window.location.replace("/");
        });
    } else {
      response?.data?.message &&
        popAlert("Error!", response?.data?.message, "error");
      response?.data?.data && setErrors(response.data.data);
    }
    setLoading(false);
  };

  return (
    <React.Fragment>
      <Box
        sx={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          height: "100vh",
          backgroundColor: colors.secondary,
        }}
      >
        <Box
          sx={{
            borderRadius: "5px",
            boxShadow: "0px 2px 5px rgba(0, 0, 0, 0.25)",
            p: 5,
            width: 400,
            textAlign: "center",
          }}
        >
          <Box sx={{ mb: 2 }}>
            <Typography
              variant="h4"
              fontWeight="bold"
              color="primary"
              textAlign={"center"}
              sx={{ mb: 6 }}
            >
              Sign In
            </Typography>
            <form onSubmit={handleSubmit}>
              <Box sx={{ mb: 2, m: 3 }}>
                <TextField
                  id="outlined-basic"
                  variant="filled"
                  label="username"
                  fullWidth
                  value={inputs.username}
                  onChange={(e) =>
                    setInputs({
                      ...inputs,
                      username: e.target.value,
                    })
                  }
                />
                {errors["username"] && (
                  <Typography color="error">{errors["username"]}</Typography>
                )}
              </Box>

              <Box sx={{ mb: 2, m: 3, mt: 1 }}>
                <TextField
                  id="outlined-password-input"
                  variant="filled"
                  label="Password"
                  type="password"
                  fullWidth
                  value={inputs.password}
                  onChange={(e) =>
                    setInputs({
                      ...inputs,
                      password: e.target.value,
                    })
                  }
                />
                {errors["password"] && (
                  <Typography color="error">{errors["password"]}</Typography>
                )}
              </Box>

              <Box sx={{ cursor: "pointer" }}>
                <Typography variant="h7" color="primary">
                  Forget Your Password?
                </Typography>
              </Box>
              <Box sx={{ m: 2 }}>
                <Button
                  type="submit"
                  variant="contained"
                  fullWidth
                  disabled={loading}
                >
                  {loading ? <CircularProgress color="secondary" /> : "Sign In"}
                </Button>
              </Box>
            </form>
            <Box textAlign={"center"} sx={{ cursor: "pointer" }}>
              <Typography variant="h7" color="primary">
                Do you need to create an account?
              </Typography>
            </Box>
          </Box>
        </Box>
      </Box>
    </React.Fragment>
  );
};

export default SignIn;
